const dataCards=[{id:1,title:"Nike",price:100},{id:2,title:"Nord Face",price:300},{id:3,title:"Leica",price:500}],shop=document.querySelector(".cards"),cartArray=[];function renderCard(e){const t=document.querySelector(".template").content.querySelector(".card").cloneNode(!0),r=t.querySelector(".card__title"),c=t.querySelector(".card__price-number");r.textContent=e.title,c.textContent=e.price;return t.querySelector(".card__btn").addEventListener("click",()=>{renderBusket(e),console.log("Add card",{cartArray:cartArray})}),t}function initialCardsToPage(e,t){t.append(renderCard(e))}dataCards.forEach(e=>{initialCardsToPage(e,shop)});const cartPriceTotal=document.querySelector(".cart__price-total"),cartInner=document.querySelector(".cart__inner");function renderBusket(e){const t=document.querySelector(".template-product").content.querySelector(".product").cloneNode(!0);t.querySelector(".product__title").textContent=e.title,t.querySelector(".product__price").textContent=e.price,t.querySelector(".product__remove").addEventListener("click",t=>{t.target.closest(".product").remove(),deletaCartToBusket(e)}),cartArray.find(t=>t.id==e.id)?alert("product added")&&console.log("btn-disable"):(cartArray.push(e),cartInner.append(t));const r=cartArray.map(e=>e.price).reduce((e,t)=>e+t,0);cartPriceTotal.textContent=r+" $"}function deletaCartToBusket(e){cartArray.forEach((t,r)=>{t.id===e.id&&cartArray.splice(r,1)});const t=cartArray.map(e=>e.price).reduce((e,t)=>e+t,0);cartPriceTotal.textContent=t+" $"}