const dataCards=[{id:1,title:"Nike",price:100},{id:2,title:"Nord Face",price:300},{id:3,title:"Leica",price:500}],shop=document.querySelector(".cards");function addCardsToShop(e){const t=document.querySelector(".template").content.querySelector(".card").cloneNode(!0),r=t.querySelector(".card__title"),c=t.querySelector(".card__price-number");return r.textContent=e.title,c.textContent=e.price,t.querySelector(".card__btn").addEventListener("click",()=>{cardToBasket(e)}),t}function addCards(e,t){t.append(addCardsToShop(e))}dataCards.forEach(e=>{addCards(e,shop)});const cartPriceTotal=document.querySelector(".cart__price-total"),cartArray=[],cartInner=document.querySelector(".cart__inner");function cardToBasket(e){const t=document.querySelector(".template-product").content.querySelector(".product").cloneNode(!0);if(t.querySelector(".product__title").textContent=e.title,t.querySelector(".product__price").textContent=e.price,t.querySelector(".product__remove").addEventListener("click",t=>{t.target.closest(".product").remove(),cartArray.forEach((t,r)=>{t.id===e.id&&cartArray.splice(r,1)});const r=cartArray.map(e=>e.price).reduce((e,t)=>e+t,0);cartPriceTotal.textContent=r+" $",console.log({sum:r})}),cartArray.find(t=>t.id===e.id))alert("product added");else{cartInner.append(t),cartArray.push(e);const r=cartArray.map(e=>e.price).reduce((e,t)=>e+t,0);cartPriceTotal.textContent=r+" $"}}