import dataCards from"./data.js";const shop=document.querySelector(".cards");function addCardsToShop(e){const t=document.querySelector(".template").content.querySelector(".card").cloneNode(!0),r=t.querySelector(".card__title"),o=t.querySelector(".card__price-number");return r.textContent=e.title,o.textContent=e.price,t}function addCards(e,t){const r=addCardsToShop(e);t.append(r)}dataCards.forEach(e=>{addCards(e,shop)});