const dataCards=[{title:"Nike",price:100},{title:"Nord Face",price:300},{title:"Leica",price:500}],shop=document.querySelector(".cards");function addCardsToShop(e){const t=document.querySelector(".template").content.querySelector(".card").cloneNode(!0),r=t.querySelector(".card__title"),c=t.querySelector(".card__price-number");return r.textContent=e.title,c.textContent=e.price,t.querySelector(".card__btn").addEventListener("click",()=>{console.log("click")}),t}function addCards(e,t){t.append(addCardsToShop(e))}dataCards.forEach(e=>{addCards(e,shop)});